const Projects = () => {
  const [selectedProject, setSelectedProject] = useState<Project | null>(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  useEffect(() => {
    const interval = setInterval(() => {
      if (scrollContainerRef.current) {
        const container = scrollContainerRef.current;
        const scrollAmount = container.offsetWidth * 0.8;
        const maxScroll = container.scrollWidth - container.offsetWidth;
        
        if (container.scrollLeft >= maxScroll) {
          container.scrollTo({ left: 0, behavior: 'smooth' });
        } else {
          container.scrollTo({
            left: container.scrollLeft + scrollAmount,
            behavior: 'smooth'
          });
        }
      }
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const handleOpenDialog = (project: Project) => {
    setSelectedProject(project);
  };

  const handleCloseDialog = () => {
    setSelectedProject(null);
  };

  const handleDemoClick = (url: string) => {
    window.open(url, '_blank', 'width=800,height=600');
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'linear-gradient(180deg, rgba(15, 23, 42, 0.8) 0%, rgba(15, 23, 42, 0.95) 100%)',
      py: 8
    }}>
      <Container maxWidth="xl">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Typography
            variant="h2"
            component="h1"
            gutterBottom
            align="center"
            sx={{
              mb: 2,
              background: 'linear-gradient(45deg, #6366f1 30%, #ec4899 90%)',
              backgroundClip: 'text',
              textFillColor: 'transparent',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              fontSize: { xs: '2.5rem', md: '3.5rem' },
              fontWeight: 700,
            }}
          >
            프로젝트
          </Typography>
          <Typography
            variant="h5"
            color="text.secondary"
            paragraph
            align="center"
            sx={{ 
              mb: 8, 
              maxWidth: '800px', 
              mx: 'auto',
              fontSize: { xs: '1.1rem', md: '1.25rem' },
              lineHeight: 1.6,
            }}
          >
            제가 작업한 주요 프로젝트들을 소개합니다
          </Typography>
        </motion.div>

        <Box sx={{ position: 'relative', px: { xs: 1, sm: 2, md: 3 } }}>
          <Box
            ref={scrollContainerRef}
            sx={{
              display: 'flex',
              overflowX: 'auto',
              scrollSnapType: 'x mandatory',
              gap: 3,
              pb: 4,
              '&::-webkit-scrollbar': { display: 'none' },
              msOverflowStyle: 'none',
              scrollbarWidth: 'none',
            }}
          >
            {projects.map((project, index) => (
              <Box
                key={project.id}
                sx={{
                  flex: '0 0 auto',
                  width: { xs: '85%', sm: '70%', md: '50%', lg: '35%' },
                  scrollSnapAlign: 'start',
                }}
              >
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <Card
                    sx={{
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column',
                      bgcolor: 'rgba(255, 255, 255, 0.05)',
                      backdropFilter: 'blur(10px)',
                      borderRadius: '24px',
                      border: '1px solid rgba(255, 255, 255, 0.1)',
                      transition: 'all 0.3s ease-in-out',
                      '&:hover': {
                        transform: 'translateY(-8px)',
                        boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
                        bgcolor: 'rgba(255, 255, 255, 0.08)',
                      },
                    }}
                  >
                    <CardMedia
                      component="img"
                      height="240"
                      image={project.image}
                      alt={project.title}
                      sx={{ 
                        objectFit: 'cover',
                        borderTopLeftRadius: '24px',
                        borderTopRightRadius: '24px',
                      }}
                    />
                    <CardContent sx={{ flexGrow: 1, p: 3 }}>
                      <Typography 
                        variant="h5" 
                        component="h2" 
                        gutterBottom
                        sx={{ 
                          fontWeight: 600,
                          mb: 2,
                          background: 'linear-gradient(45deg, #6366f1 30%, #ec4899 90%)',
                          backgroundClip: 'text',
                          textFillColor: 'transparent',
                          WebkitBackgroundClip: 'text',
                          WebkitTextFillColor: 'transparent',
                        }}
                      >
                        {project.title}
                      </Typography>
                      <Typography 
                        color="text.secondary" 
                        paragraph
                        sx={{ 
                          mb: 3,
                          lineHeight: 1.7,
                          fontSize: '1rem',
                        }}
                      >
                        {project.description}
                      </Typography>
                      <Box sx={{ mb: 3 }}>
                        {project.technologies.map((tech) => (
                          <Chip
                            key={tech}
                            label={tech}
                            size="small"
                            sx={{
                              mr: 1,
                              mb: 1,
                              bgcolor: 'rgba(99, 102, 241, 0.1)',
                              color: 'primary.main',
                              fontWeight: 500,
                              '&:hover': {
                                bgcolor: 'rgba(99, 102, 241, 0.2)',
                              },
                            }}
                          />
                        ))}
                      </Box>
                      <Box sx={{ display: 'flex', gap: 2 }}>
                        <Button
                          variant="contained"
                          onClick={() => handleDemoClick(project.demo)}
                          sx={{ 
                            flex: 1,
                            py: 1.5,
                            borderRadius: '12px',
                            textTransform: 'none',
                            fontSize: '1rem',
                            fontWeight: 600,
                            background: 'linear-gradient(45deg, #6366f1 30%, #ec4899 90%)',
                            '&:hover': {
                              background: 'linear-gradient(45deg, #4f46e5 30%, #db2777 90%)',
                            },
                          }}
                        >
                          데모 보기
                        </Button>
                        <Button
                          variant="outlined"
                          onClick={() => handleOpenDialog(project)}
                          sx={{ 
                            flex: 1,
                            py: 1.5,
                            borderRadius: '12px',
                            textTransform: 'none',
                            fontSize: '1rem',
                            fontWeight: 600,
                            borderColor: 'rgba(255, 255, 255, 0.2)',
                            color: 'text.primary',
                            '&:hover': {
                              borderColor: 'primary.main',
                              bgcolor: 'rgba(99, 102, 241, 0.1)',
                            },
                          }}
                        >
                          상세 보기
                        </Button>
                      </Box>
                    </CardContent>
                  </Card>
                </motion.div>
              </Box>
            ))}
          </Box>
        </Box>
      </Container>

      <Dialog
        open={!!selectedProject}
        onClose={handleCloseDialog}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            bgcolor: 'background.paper',
            borderRadius: '24px',
            border: '1px solid rgba(255, 255, 255, 0.1)',
          }
        }}
      >
        {selectedProject && (
          <>
            <DialogTitle sx={{ 
              pb: 2,
              background: 'linear-gradient(45deg, #6366f1 30%, #ec4899 90%)',
              backgroundClip: 'text',
              textFillColor: 'transparent',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              fontWeight: 600,
            }}>
              {selectedProject.title}
              <IconButton
                onClick={handleCloseDialog}
                sx={{ 
                  position: 'absolute', 
                  right: 16, 
                  top: 16,
                  color: 'text.secondary',
                  '&:hover': {
                    color: 'primary.main',
                  },
                }}
              >
                <CloseIcon />
              </IconButton>
            </DialogTitle>
            <DialogContent>
              <Grid container spacing={3} sx={{ mt: 1 }}>
                {selectedProject.detailImages.map((image, index) => (
                  <Grid item xs={12} md={4} key={index}>
                    <Box
                      component="img"
                      src={image}
                      alt={`${selectedProject.title} 상세 이미지 ${index + 1}`}
                      sx={{
                        width: '100%',
                        height: '200px',
                        borderRadius: '16px',
                        objectFit: 'cover',
                        transition: 'transform 0.3s ease-in-out',
                        '&:hover': {
                          transform: 'scale(1.05)',
                        },
                      }}
                    />
                  </Grid>
                ))}
              </Grid>
              <Box sx={{ mt: 4 }}>
                <Typography 
                  variant="h6" 
                  gutterBottom
                  sx={{ 
                    fontWeight: 600,
                    color: 'primary.main',
                  }}
                >
                  담당 파트
                </Typography>
                <Typography 
                  color="text.secondary"
                  sx={{ 
                    lineHeight: 1.8,
                    fontSize: '1.1rem',
                  }}
                >
                  {selectedProject.myPart}
                </Typography>
              </Box>
            </DialogContent>
            <DialogActions sx={{ px: 3, pb: 3 }}>
              <Button 
                onClick={handleCloseDialog}
                variant="contained"
                sx={{ 
                  borderRadius: '12px',
                  textTransform: 'none',
                  px: 4,
                  py: 1,
                }}
              >
                닫기
              </Button>
            </DialogActions>
          </>
        )}
      </Dialog>
    </Box>
  );
};

export default Projects;
