import { Container, Typography, Box, Grid, Paper } from '@mui/material';
import { motion } from 'framer-motion';
import CodeIcon from '@mui/icons-material/Code';
import BrushIcon from '@mui/icons-material/Brush';
import SpeedIcon from '@mui/icons-material/Speed';

const skills = [
  {
    icon: <CodeIcon sx={{ fontSize: 40 }} />,
    title: '프론트엔드 개발',
    description: 'React, TypeScript, HTML5, CSS3를 활용한 웹 애플리케이션 개발',
  },
  {
    icon: <BrushIcon sx={{ fontSize: 40 }} />,
    title: 'UI/UX 디자인',
    description: '사용자 중심의 직관적인 인터페이스 설계 및 구현',
  },
  {
    icon: <SpeedIcon sx={{ fontSize: 40 }} />,
    title: '성능 최적화',
    description: '빠른 로딩 속도와 부드러운 사용자 경험을 위한 최적화',
  },
];

const About = () => {
  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Typography variant="h2" component="h1" gutterBottom align="center">
            소개
          </Typography>
          <Typography variant="h5" color="text.secondary" paragraph align="center" sx={{ mb: 6 }}>
            프론트엔드 개발자로서의 제 이야기를 들려드립니다
          </Typography>
        </motion.div>

        <Grid container spacing={4}>
          {skills.map((skill, index) => (
            <Grid item xs={12} md={4} key={index}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 3,
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    textAlign: 'center',
                  }}
                >
                  <Box sx={{ color: 'primary.main', mb: 2 }}>{skill.icon}</Box>
                  <Typography variant="h5" component="h3" gutterBottom>
                    {skill.title}
                  </Typography>
                  <Typography color="text.secondary">{skill.description}</Typography>
                </Paper>
              </motion.div>
            </Grid>
          ))}
        </Grid>

        <Box sx={{ mt: 6 }}>
          <Typography variant="h4" gutterBottom>
            경력 및 교육
          </Typography>
          <Typography paragraph>
            • 2020-2023: 프론트엔드 개발자 @테크 컴퍼니
          </Typography>
          <Typography paragraph>
            • 2016-2020: 컴퓨터 공학 학사 @한국대학교
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default About;
