import { useState, useRef, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  useMediaQuery,
  useTheme,
} from '@mui/material';
import { motion } from 'framer-motion';
import CloseIcon from '@mui/icons-material/Close';

interface Project {
  id: number;
  title: string;
  description: string;
  image: string;
  technologies: string[];
  demo: string;
  detailImages: string[];
  myPart: string;
}

const projects: Project[] = [
  {
    id: 1,
    title: '포트폴리오 웹사이트',
    description: 'React와 TypeScript를 사용한 개인 포트폴리오 웹사이트입니다.',
    image: '/img/projectImg/1_khoa.png',
    technologies: ['React', 'TypeScript', 'Material-UI', 'Framer Motion'],
    demo: 'https://example.com/portfolio',
    detailImages: ['/img/projectImg/1portfolio.png', '/img/projectImg/2portfolio.png', '/img/projectImg/3portfolio.png'],
    myPart: '전체 개발 및 디자인을 담당했습니다. React와 TypeScript를 사용하여 모던한 UI/UX를 구현했으며, Material-UI를 활용하여 반응형 디자인을 적용했습니다.'
  },
  {
    id: 2,
    title: '쇼핑몰 프로젝트',
    description: 'Spring Boot와 React를 사용한 풀스택 쇼핑몰 프로젝트입니다.',
    image: '/img/projectImg/shopping.png',
    technologies: ['Spring Boot', 'React', 'MySQL', 'AWS'],
    demo: 'https://example.com/shopping',
    detailImages: ['/img/projectImg/1shopping.png', '/img/projectImg/2shopping.png', '/img/projectImg/3shopping.png'],
    myPart: '백엔드 API 개발을 담당했습니다. Spring Boot를 사용하여 RESTful API를 구현했으며, MySQL 데이터베이스 설계 및 AWS 배포를 진행했습니다.'
  },
  {
    id: 3,
    title: '소셜 미디어 앱',
    description: '실시간 채팅과 게시물 공유 기능이 있는 소셜 미디어 애플리케이션입니다.',
    image: '/img/projectImg/social.png',
    technologies: ['Node.js', 'React', 'Socket.io', 'MongoDB'],
    demo: 'https://example.com/social',
    detailImages: ['/img/projectImg/1social.png', '/img/projectImg/2social.png', '/img/projectImg/3social.png'],
    myPart: '실시간 채팅 기능 개발을 담당했습니다. Socket.io를 사용하여 실시간 통신을 구현했으며, MongoDB를 활용하여 데이터를 저장하고 관리했습니다.'
  },
  {
    id: 4,
    title: '해양 모니터링 시스템',
    description: '해양 환경 데이터를 실시간으로 모니터링하는 시스템입니다.',
    image: '/img/projectImg/marine.png',
    technologies: ['Java', 'Spring', 'PostgreSQL', 'OpenLayers'],
    demo: 'https://example.com/marine',
    detailImages: ['/img/projectImg/1marine.png', '/img/projectImg/2marine.png', '/img/projectImg/3marine.png'],
    myPart: 'GIS 시스템 개발을 담당했습니다. OpenLayers를 사용하여 해양 데이터를 시각화했으며, 실시간 데이터 처리 시스템을 구축했습니다.'
  },
  {
    id: 5,
    title: '자율운항선박 관리 시스템',
