import { useState, useRef } from 'react';
import {
  Container,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  useMediaQuery,
  useTheme,
} from '@mui/material';
import { motion } from 'framer-motion';
import CloseIcon from '@mui/icons-material/Close';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

interface Project {
  id: number;
  title: string;
  description: string;
  image: string;
  technologies: string[];
  demo: string;
  detailImages: string[];
  myPart: string;
}

const projects: Project[] = [
  {
    id: 1,
    title: '포트폴리오 웹사이트',
    description: 'React와 TypeScript를 사용한 개인 포트폴리오 웹사이트입니다.',
    image: '/img/portfolio.jpg',
    technologies: ['React', 'TypeScript', 'Material-UI', 'Framer Motion'],
    demo: 'https://example.com/portfolio',
    detailImages: ['/img/portfolio1.jpg', '/img/portfolio2.jpg', '/img/portfolio3.jpg'],
    myPart: '전체 개발 및 디자인을 담당했습니다. React와 TypeScript를 사용하여 모던한 UI/UX를 구현했으며, Material-UI를 활용하여 반응형 디자인을 적용했습니다.'
  },
  {
    id: 2,
    title: '쇼핑몰 프로젝트',
    description: 'Spring Boot와 React를 사용한 풀스택 쇼핑몰 프로젝트입니다.',
    image: '/img/shopping.jpg',
    technologies: ['Spring Boot', 'React', 'MySQL', 'AWS'],
    demo: 'https://example.com/shopping',
    detailImages: ['/img/shopping1.jpg', '/img/shopping2.jpg', '/img/shopping3.jpg'],
    myPart: '백엔드 API 개발을 담당했습니다. Spring Boot를 사용하여 RESTful API를 구현했으며, MySQL 데이터베이스 설계 및 AWS 배포를 진행했습니다.'
  },
  {
    id: 3,
    title: '소셜 미디어 앱',
    description: '실시간 채팅과 게시물 공유 기능이 있는 소셜 미디어 애플리케이션입니다.',
    image: '/img/social.jpg',
    technologies: ['Node.js', 'React', 'Socket.io', 'MongoDB'],
    demo: 'https://example.com/social',
    detailImages: ['/img/social1.jpg', '/img/social2.jpg', '/img/social3.jpg'],
    myPart: '실시간 채팅 기능 개발을 담당했습니다. Socket.io를 사용하여 실시간 통신을 구현했으며, MongoDB를 활용하여 데이터를 저장하고 관리했습니다.'
  },
  {
    id: 4,
    title: '해양 모니터링 시스템',
    description: '해양 환경 데이터를 실시간으로 모니터링하는 시스템입니다.',
    image: '/img/marine.jpg',
    technologies: ['Java', 'Spring', 'PostgreSQL', 'OpenLayers'],
    demo: 'https://example.com/marine',
    detailImages: ['/img/marine1.jpg', '/img/marine2.jpg', '/img/marine3.jpg'],
    myPart: 'GIS 시스템 개발을 담당했습니다. OpenLayers를 사용하여 해양 데이터를 시각화했으며, 실시간 데이터 처리 시스템을 구축했습니다.'
  },
  {
    id: 5,
    title: '자율운항선박 관리 시스템',
    description: '자율운항선박의 상태를 모니터링하고 제어하는 시스템입니다.',
    image: '/img/autonomous.jpg',
    technologies: ['Java', 'Spring', 'MySQL', 'WebSocket'],
    demo: 'https://example.com/autonomous',
    detailImages: ['/img/autonomous1.jpg', '/img/autonomous2.jpg', '/img/autonomous3.jpg'],
