import { Container, Grid, Card, CardContent, CardMedia, Typography, Box } from '@mui/material';
import { motion } from 'framer-motion';

const projects = [
  {
    id: 1,
    title: '포트폴리오 웹사이트',
    description: 'React와 TypeScript를 사용한 반응형 포트폴리오 웹사이트',
    image: '/images/portfolio.jpg',
    technologies: ['React', 'TypeScript', 'Material-UI', 'Framer Motion'],
  },
  {
    id: 2,
    title: '쇼핑몰 프로젝트',
    description: '전자상거래 플랫폼 구현',
    image: '/images/shopping.jpg',
    technologies: ['React', 'Redux', 'Node.js', 'MongoDB'],
  },
  {
    id: 3,
    title: '소셜 미디어 앱',
    description: '실시간 채팅과 게시물 공유 기능이 있는 소셜 미디어 애플리케이션',
    image: '/images/social.jpg',
    technologies: ['React', 'Firebase', 'Tailwind CSS', 'WebSocket'],
  },
];

const Projects = () => {
  return (
    <Container maxWidth="lg">
      <Typography variant="h2" component="h1" gutterBottom align="center" sx={{ my: 4 }}>
        프로젝트
      </Typography>
      <Grid container spacing={4}>
        {projects.map((project) => (
          <Grid item xs={12} md={6} lg={4} key={project.id}>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="200"
                  image={project.image}
                  alt={project.title}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {project.title}
                  </Typography>
                  <Typography paragraph color="text.secondary">
                    {project.description}
                  </Typography>
                  <Box sx={{ mt: 2 }}>
                    {project.technologies.map((tech) => (
                      <Typography
                        key={tech}
                        component="span"
                        sx={{
                          mr: 1,
                          mb: 1,
                          px: 1,
                          py: 0.5,
                          bgcolor: 'primary.main',
                          color: 'white',
                          borderRadius: 1,
                          display: 'inline-block',
                          fontSize: '0.875rem',
                        }}
                      >
                        {tech}
                      </Typography>
                    ))}
                  </Box>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Projects;
