import { Container, Typography, Box, Grid, Paper, Avatar } from '@mui/material';
import { motion } from 'framer-motion';
import CodeIcon from '@mui/icons-material/Code';
import BrushIcon from '@mui/icons-material/Brush';
import SpeedIcon from '@mui/icons-material/Speed';

const skills = [
  {
    icon: <CodeIcon sx={{ fontSize: 40 }} />,
    title: '프론트엔드 개발',
    description: 'React, TypeScript, HTML5, CSS3를 활용한 웹 애플리케이션 개발',
  },
  {
    icon: <BrushIcon sx={{ fontSize: 40 }} />,
    title: 'UI/UX 디자인',
    description: '사용자 중심의 직관적인 인터페이스 설계 및 구현',
  },
  {
    icon: <SpeedIcon sx={{ fontSize: 40 }} />,
    title: '성능 최적화',
    description: '빠른 로딩 속도와 부드러운 사용자 경험을 위한 최적화',
  },
];

const About = () => {
  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <Typography
            variant="h2"
            component="h1"
            gutterBottom
            align="center"
            sx={{
              mb: 2,
              background: 'linear-gradient(45deg, #6366f1 30%, #ec4899 90%)',
              backgroundClip: 'text',
              textFillColor: 'transparent',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          >
            소개
          </Typography>
          <Typography
            variant="h5"
            color="text.secondary"
            paragraph
            align="center"
            sx={{ mb: 8, maxWidth: '800px', mx: 'auto' }}
          >
            프론트엔드 개발자로서의 제 이야기를 들려드립니다
          </Typography>
        </motion.div>

        <Grid container spacing={4} sx={{ mb: 8 }}>
          <Grid item xs={12} md={4}>
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
            >
              <Box
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  textAlign: 'center',
                }}
              >
                <Avatar
                  src="/images/profile.jpg"
                  alt="Profile"
                  sx={{
                    width: 200,
                    height: 200,
                    mb: 3,
                    border: '4px solid',
                    borderColor: 'primary.main',
                  }}
                />
                <Typography variant="h4" gutterBottom>
                  홍길동
                </Typography>
                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                  프론트엔드 개발자
                </Typography>
              </Box>
            </motion.div>
          </Grid>
          <Grid item xs={12} md={8}>
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
            >
              <Paper
                elevation={0}
                sx={{
                  p: 4,
                  height: '100%',
                  bgcolor: 'background.paper',
                  borderRadius: '16px',
                  border: '1px solid rgba(255, 255, 255, 0.1)',
                }}
              >
                <Typography variant="h5" gutterBottom>
                  자기소개
                </Typography>
                <Typography paragraph color="text.secondary" sx={{ mb: 4 }}>
                  안녕하세요! 저는 3년차 프론트엔드 개발자입니다. 사용자 경험을 최우선으로 생각하며,
                  깔끔하고 효율적인 코드 작성을 지향합니다. 새로운 기술을 배우는 것을 좋아하며,
                  팀과의 협업을 통해 더 나은 결과물을 만들어내는 것을 즐깁니다.
                </Typography>
                <Typography variant="h5" gutterBottom>
                  경력 및 교육
                </Typography>
                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                    2020-2023: 프론트엔드 개발자 @테크 컴퍼니
                  </Typography>
                  <Typography color="text.secondary" paragraph>
                    • React와 TypeScript를 활용한 웹 애플리케이션 개발
                    • 사용자 경험 개선 및 성능 최적화
                    • 팀 리드 및 멘토링
                  </Typography>
                </Box>
                <Box>
                  <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                    2016-2020: 컴퓨터 공학 학사 @한국대학교
                  </Typography>
                  <Typography color="text.secondary">
                    • 웹 개발 동아리 회장
                    • 졸업 프로젝트 우수상
                  </Typography>
                </Box>
              </Paper>
            </motion.div>
          </Grid>
        </Grid>

        <Typography
          variant="h3"
          gutterBottom
          align="center"
          sx={{ mb: 6, fontWeight: 600 }}
        >
          기술 스택
        </Typography>

        <Grid container spacing={4}>
          {skills.map((skill, index) => (
            <Grid item xs={12} md={4} key={index}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
              >
                <Paper
                  elevation={0}
                  sx={{
                    p: 4,
                    height: '100%',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    textAlign: 'center',
                    bgcolor: 'background.paper',
                    borderRadius: '16px',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    transition: 'transform 0.3s ease-in-out',
                    '&:hover': {
                      transform: 'translateY(-8px)',
                    },
                  }}
                >
                  <Box
                    sx={{
                      color: 'primary.main',
                      mb: 2,
                      p: 2,
                      borderRadius: '12px',
                      bgcolor: 'rgba(99, 102, 241, 0.1)',
                    }}
                  >
                    {skill.icon}
                  </Box>
                  <Typography variant="h5" component="h3" gutterBottom>
                    {skill.title}
                  </Typography>
                  <Typography color="text.secondary">
                    {skill.description}
                  </Typography>
                </Paper>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default About;
